mapper.update:
		UPDATE MEMBER
   		SET MEMBER_ID=#{mId}, MEMBER_NICK=#{mNick}, ABOUT_ME=#{aboutMe}
   		WHERE MEMBER_NO=#{mNo}

UPDATE MEMBER
SET AUTHKEY=#{authKey}
WHERE MEMBER_ID=#{mId}

UPDATE MEMBER
SET MEMBER_LEVEL=#{mLevel}
WHERE MEMBER_ID=#{mId}

mapper.select:
SELECT COUNT(*)
FROM MEMBERVIEW
WHERE MEMBER_ID=#{mId}

SELECT COUNT(*)
FROM MEMBERVIEW
WHERE MEMBER_NICK=#{mNick}
